import Reat, { useState } from 'react'
import auth0 from '../lib/auth0'

const CreateStatus = () => {
    const [dados, setDados] = useState ({
        status: 'bem',
        coords:{
            lat: null,
            long: null
        }
    })
    const getMyLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(position => {
                console.log(position.coords)
            })
        }
    }
    return (
        <div>
            <h1>Create Status</h1>
            <label className='block'><
                input type='radio' name='status' value='bem' />Estou bem e sem sitomas.
                </label>
            <label className='block'>
                <input type='radio' name='status' value='gripe' />Estou com sintomas de gripe/resfriado.
                </label>
            <label className='block'><
                input type='radio' name='status' value='covid' />Estou com sintomas de COVID.
                </label>
                Sua posição Atual: {JSON.stringify(dados)}
            <button onClick={getMyLocation}>Check my location</button>
        </div>
    )
}
export default CreateStatus

export async function getServerSideProps({ req, res }) {
    const session = await auth0.getSession(req)

    if (session) {
        return {
            props: {
                isAuth: true,
                user: session.user,
            }
        }
    }
    return {
        props: {
            isAuth: false,
            user: {}
        }
    }
}
